import { SIGN_IN } from './types'
import { SIGN_OUT } from './types'

export const signIn = (userId) => {
  return {
    type: SIGN_IN,
    payload: userId,
  }
}

export const signOut = () => {
  return { type: SIGN_OUT }
}
